import React, { Component } from 'react';
import DataTable from 'react-data-table-component';
import axios from 'axios';

class PagesTable extends Component {
  constructor() {
    super();
    this.state = {
      pages: [],
      loading: true
    };
  }

  componentDidMount() {
    axios.get('http://localhost:5000/users/all-pages')
      .then(res => {
        this.setState({ pages: res.data, loading: false });
      })
      .catch(err => {
        console.error(err);
        this.setState({ loading: false });
      });
  }

  render() {
    const columns = [
      {
        name: 'ID',
        selector: row => row.id,
        sortable: true,
      },
      {
        name: 'Title',
        selector: row => row.title,
        sortable: true,
      },
      {
        name: 'Content',
        selector: row => row.content,
        sortable: true,
      },
      {
        name: 'Image',
        selector: row => row.img,
        cell: row => <img src={`http://localhost:5000/uploads/${row.img}`} alt={row.title} width="50" />,
      },
      {
        name: 'Actions',
        cell: row => (
          <div>
            <button
              className="btn btn-warning btn-sm mr-2"
              onClick={() => this.handleEdit(row.id)}
            >
              Edit
            </button>
            <button
              className="btn btn-danger btn-sm"
              onClick={() => this.handleDelete(row.id)}
            >
              Delete
            </button>
          </div>
        ),
        ignoreRowClick: true,
        allowOverflow: true,
        button: true,
      }
    ];

    return (
      <div className="container mt-5">
        <h1 className="text-center mb-4">Pages Data Table</h1>
        <DataTable
          columns={columns}
          data={this.state.pages}
          progressPending={this.state.loading}
          pagination
        />
      </div>
    );
  }
}

export default PagesTable;
